import React, { useState } from 'react';
import { Upload, Search, FileText, Brain, Loader, ChevronDown, ChevronUp, Filter } from 'lucide-react';

const App = () => {
  const [currentView, setCurrentView] = useState('upload'); // 'upload', 'form', 'search'
  const [uploadedFile, setUploadedFile] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [formData, setFormData] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [selectedFilters, setSelectedFilters] = useState({
    field: [],
    method: [],
    application: [],
    beamline: [],
    year: []
  });

  // サンプル研究データ (Sample Research Data)
  const samplePapers = [
    {
      id: 1,
      title: '高エネルギーX線回折による新規触媒材料の構造解析',
      titleEn: 'Structural Analysis of Novel Catalyst Materials using High-Energy X-ray Diffraction',
      authors: 'Hatsui, T., Takeshita, K.',
      year: 2024,
      field: '材料科学 (Materials Science)',
      method: 'X線回折 (X-ray Diffraction)',
      beamline: 'BL19B2',
      application: '自動車産業 (Automotive)',
      mainConclusion: '新規Pt-Co合金触媒の原子配列構造を解明し、触媒活性が30%向上することを実証',
      industrialApplication: '自動車用燃料電池の効率向上、環境触媒技術',
      crossDomain: '化学工学、エネルギー工学への応用可能',
      failedApproach: '従来のTEM観察では動的変化を捉えられなかった'
    },
    {
      id: 2,
      title: 'タンパク質結晶構造解析による創薬ターゲット同定',
      titleEn: 'Drug Target Identification through Protein Crystal Structure Analysis',
      authors: 'Yamamoto, M., Tanaka, Y.',
      year: 2024,
      field: '生命科学 (Life Sciences)',
      method: 'X線結晶構造解析 (X-ray Crystallography)',
      beamline: 'BL41XU',
      application: '製薬・医療 (Pharmaceutical)',
      mainConclusion: '新規抗がん剤標的タンパク質の活性部位構造を原子レベルで解明',
      industrialApplication: '製薬業界における新薬開発、個別化医療',
      crossDomain: '構造生物学、計算化学への展開',
      failedApproach: '低温凍結条件では生理的構造が維持できなかった'
    },
    {
      id: 3,
      title: 'ナノ材料の時間分解分光による電子状態ダイナミクス研究',
      titleEn: 'Electronic State Dynamics of Nanomaterials via Time-Resolved Spectroscopy',
      authors: 'Sato, K., Nakamura, H.',
      year: 2023,
      field: 'ナノサイエンス (Nanoscience)',
      method: '時間分解分光 (Time-Resolved Spectroscopy)',
      beamline: 'BL43IR',
      application: 'エネルギー産業 (Energy)',
      mainConclusion: '量子ドットの励起状態寿命が表面修飾により10倍延長可能',
      industrialApplication: '高効率太陽電池、量子コンピュータ素子開発',
      crossDomain: '半導体工学、光エレクトロニクス',
      failedApproach: '室温測定では熱揺らぎの影響が大きすぎた'
    },
    {
      id: 4,
      title: '高分子材料の放射光X線小角散乱による階層構造解析',
      titleEn: 'Hierarchical Structure Analysis of Polymer Materials by Synchrotron SAXS',
      authors: 'Kobayashi, R., Ishida, M.',
      year: 2023,
      field: '高分子科学 (Polymer Science)',
      method: '小角X線散乱 (SAXS)',
      beamline: 'BL40B2',
      application: '化学産業 (Chemical)',
      mainConclusion: '新規生分解性プラスチックのナノ構造制御により強度が2倍向上',
      industrialApplication: '環境対応型プラスチック製品、包装材料の高性能化',
      crossDomain: '材料工学、環境科学への応用',
      failedApproach: '従来のDSC測定では階層構造の詳細が不明瞭だった'
    },
    {
      id: 5,
      title: '磁性材料のX線磁気円二色性による電子状態観察',
      titleEn: 'Electronic State Observation of Magnetic Materials by XMCD',
      authors: 'Fujimoto, S., Watanabe, K.',
      year: 2024,
      field: '物性物理学 (Condensed Matter Physics)',
      method: 'X線磁気円二色性 (XMCD)',
      beamline: 'BL25SU',
      application: 'エレクトロニクス (Electronics)',
      mainConclusion: 'スピントロニクス材料の磁気異方性起源を原子レベルで解明',
      industrialApplication: '次世代ハードディスク、磁気メモリの高密度化',
      crossDomain: '情報工学、量子技術への展開',
      failedApproach: 'SQUID測定では局所的な磁気構造が観測できなかった'
    },
    {
      id: 6,
      title: '半導体デバイスの高速X線イメージングによる動作解析',
      titleEn: 'Operational Analysis of Semiconductor Devices by High-Speed X-ray Imaging',
      authors: 'Suzuki, H., Ito, T.',
      year: 2022,
      field: '半導体工学 (Semiconductor Engineering)',
      method: '高速X線イメージング (High-Speed X-ray Imaging)',
      beamline: 'BL20XU',
      application: 'エレクトロニクス (Electronics)',
      mainConclusion: '次世代パワー半導体の熱応力分布をリアルタイムで可視化',
      industrialApplication: '電気自動車用パワーデバイスの信頼性向上',
      crossDomain: '電気工学、熱工学への応用',
      failedApproach: '可視光観察では内部構造の変化が観測不可能だった'
    },
    {
      id: 7,
      title: '文化財の非破壊X線分析による製作技法の解明',
      titleEn: 'Non-Destructive X-ray Analysis for Understanding Cultural Heritage Production Techniques',
      authors: 'Nakamura, Y., Kimura, A.',
      year: 2023,
      field: '文化財科学 (Cultural Heritage Science)',
      method: 'X線蛍光分析 (XRF)',
      beamline: 'BL08W',
      application: '文化・芸術 (Culture & Arts)',
      mainConclusion: '江戸時代の陶磁器の釉薬組成と焼成温度を非破壊で特定',
      industrialApplication: '文化財保存技術、伝統工芸の復元',
      crossDomain: '考古学、歴史学への応用',
      failedApproach: '従来の顕微鏡観察では内部構造が分からなかった'
    },
    {
      id: 8,
      title: '地球深部物質の高圧X線回折実験',
      titleEn: 'High-Pressure X-ray Diffraction Experiments on Deep Earth Materials',
      authors: 'Takahashi, N., Mori, D.',
      year: 2024,
      field: '地球科学 (Earth Science)',
      method: '高圧X線回折 (High-Pressure XRD)',
      beamline: 'BL10XU',
      application: '資源探査 (Resource Exploration)',
      mainConclusion: 'マントル遷移層の鉱物相転移圧力を精密決定し、地震波速度異常を説明',
      industrialApplication: '鉱物資源探査、地震予測技術への貢献',
      crossDomain: '地震学、鉱物学への展開',
      failedApproach: '常圧実験では高圧相の構造が再現できなかった'
    },
    {
      id: 9,
      title: '食品成分の放射光赤外分光による分子構造解析',
      titleEn: 'Molecular Structure Analysis of Food Components by Synchrotron Infrared Spectroscopy',
      authors: 'Tanaka, M., Saito, E.',
      year: 2023,
      field: '食品科学 (Food Science)',
      method: '赤外分光 (Infrared Spectroscopy)',
      beamline: 'BL43IR',
      application: '食品産業 (Food Industry)',
      mainConclusion: '発酵食品中のタンパク質構造変化と風味の相関を解明',
      industrialApplication: '機能性食品開発、発酵プロセス最適化',
      crossDomain: '栄養学、微生物学への応用',
      failedApproach: '通常の赤外分光では微量成分の検出が困難だった'
    }
  ];

  // MDRCG質問項目 (MDRCG Question Items)
  const mdrcgQuestions = [
    {
      id: 'mainConclusion',
      label: '主な結論 (Main Conclusion)',
      placeholder: 'この研究で明らかになった最も重要な発見を1-2文で記述してください',
      type: 'textarea',
      required: true,
      aiDraft: '新規Pt-Co合金触媒の原子配列構造を解明し、従来触媒と比較して触媒活性が30%向上することを実証しました。'
    },
    {
      id: 'priorWork',
      label: '先行研究との関係 (Relationship to Prior Work)',
      placeholder: 'この研究が基づいている3-5つの重要な先行研究を挙げてください',
      type: 'textarea',
      required: true,
      aiDraft: '1) Smith et al. (2020)のPt触媒基礎研究\n2) Tanaka et al. (2021)の合金触媒設計理論\n3) Johnson et al. (2022)のX線回折技術改良'
    },
    {
      id: 'novelty',
      label: '新規性 (Novelty)',
      placeholder: 'この研究の独自の貢献は何ですか？',
      type: 'textarea',
      required: true,
      aiDraft: '高エネルギーX線回折とその場観察を組み合わせることで、触媒反応中の原子配列動的変化を初めてリアルタイムで捉えることに成功'
    },
    {
      id: 'unknownQuestions',
      label: '未解明の課題 (Remaining Unknown Questions)',
      placeholder: 'この研究では答えられなかった2-3の具体的な疑問',
      type: 'textarea',
      required: false,
      aiDraft: '1) 長期使用時の構造安定性\n2) 他の金属組み合わせでの再現性\n3) スケールアップ時の特性維持'
    },
    {
      id: 'failedApproach',
      label: '失敗したアプローチ (Failed Approaches)',
      placeholder: 'うまくいかなかった手法とその理由',
      type: 'textarea',
      required: false,
      aiDraft: '当初TEMでの観察を試みたが、電子線による試料ダメージと時間分解能の限界により動的変化の観察が困難でした'
    },
    {
      id: 'crossDomain',
      label: '異分野翻訳レイヤー (Cross-Domain Translation)',
      placeholder: '他分野での類似問題や応用可能性',
      type: 'textarea',
      required: true,
      aiDraft: '化学工学：反応器設計への応用\nエネルギー工学：燃料電池電極材料開発\n環境科学：排ガス浄化触媒への展開'
    },
    {
      id: 'industrialPain',
      label: '産業応用可能性 (Industrial Application Potential)',
      placeholder: 'この研究が解決できる具体的な産業課題',
      type: 'textarea',
      required: true,
      aiDraft: '自動車産業：燃料電池車の効率向上とコスト削減\n化学産業：環境触媒の高性能化\nエネルギー産業：水素製造触媒の改良'
    },
    {
      id: 'abstractPrinciple',
      label: '抽象化された原理 (Abstracted Principle - 5th Grade Level)',
      placeholder: '小学5年生でも理解できるように説明してください',
      type: 'textarea',
      required: true,
      aiDraft: '異なる金属を混ぜ合わせることで、それぞれの良いところを組み合わせた「スーパー触媒」を作ることができます。特殊なX線カメラで原子の並び方を見ながら、一番効率の良い組み合わせを見つけました。'
    },
    {
      id: 'experimentalReason',
      label: '実験設計の理由 (Experimental Design Rationale)',
      placeholder: 'なぜこのパラメータ・手法を選んだのか',
      type: 'textarea',
      required: false,
      aiDraft: '高エネルギーX線（113keV）を選択した理由：試料内部の情報を非破壊で取得可能\n温度範囲300-800K：実際の触媒反応条件に対応'
    },
    {
      id: 'scalingPossibility',
      label: 'スケーリング可能性 (Scaling Possibilities)',
      placeholder: 'この技術は異なるスケールで機能しますか？',
      type: 'textarea',
      required: false,
      aiDraft: 'ナノスケール（触媒粒子）からマクロスケール（実用触媒層）まで適用可能。ただし工業スケールでは製造プロセスの最適化が必要。'
    },
    {
      id: 'combinationPotential',
      label: '組み合わせ可能性 (Combination Potential)',
      placeholder: '他の技術と組み合わせる可能性',
      type: 'textarea',
      required: false,
      aiDraft: '機械学習による構造予測と組み合わせることで、最適組成の探索時間を大幅短縮可能。計算科学との連携で新材料設計が加速。'
    }
  ];

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type === 'application/pdf') {
      setUploadedFile(file);
      setIsProcessing(true);

      // AIによる自動ドラフト生成をシミュレート (Simulate AI auto-drafting)
      setTimeout(() => {
        const draftData = {};
        mdrcgQuestions.forEach(q => {
          if (q.aiDraft) {
            draftData[q.id] = q.aiDraft;
          }
        });
        setFormData(draftData);
        setIsProcessing(false);
        setCurrentView('form');
      }, 3000);
    }
  };

  const handleInputChange = (id, value) => {
    setFormData(prev => ({ ...prev, [id]: value }));
  };

  const handleSubmit = () => {
    alert('研究データが正常に登録されました！(Research data successfully registered!)');
    setCurrentView('search');
  };

  const toggleFilter = (category, value) => {
    setSelectedFilters(prev => {
      const current = prev[category];
      const updated = current.includes(value)
        ? current.filter(v => v !== value)
        : [...current, value];
      return { ...prev, [category]: updated };
    });
  };

  const filteredPapers = samplePapers.filter(paper => {
    const matchesSearch = searchQuery === '' || 
      paper.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      paper.titleEn.toLowerCase().includes(searchQuery.toLowerCase()) ||
      paper.mainConclusion.toLowerCase().includes(searchQuery.toLowerCase()) ||
      paper.industrialApplication.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesField = selectedFilters.field.length === 0 || selectedFilters.field.includes(paper.field);
    const matchesMethod = selectedFilters.method.length === 0 || selectedFilters.method.includes(paper.method);
    const matchesApplication = selectedFilters.application.length === 0 || selectedFilters.application.includes(paper.application);
    const matchesBeamline = selectedFilters.beamline.length === 0 || selectedFilters.beamline.includes(paper.beamline);
    const matchesYear = selectedFilters.year.length === 0 || selectedFilters.year.includes(paper.year);

    return matchesSearch && matchesField && matchesMethod && matchesApplication && matchesBeamline && matchesYear;
  });

  // アップロードビュー (Upload View)
  if (currentView === 'upload') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-8">
        <div className="max-w-4xl mx-auto">
          <div className="bg-white rounded-2xl shadow-2xl p-8">
            <div className="text-center mb-8">
              <h1 className="text-4xl font-bold text-gray-800 mb-2">
                SPring-8 MDRCG システム
              </h1>
              <p className="text-gray-600">Multi-Dimensional Research Context Graph System</p>
            </div>

            <div className="flex justify-center gap-4 mb-8">
              <button
                onClick={() => setCurrentView('upload')}
                className="px-6 py-2 bg-blue-600 text-white rounded-lg font-semibold"
              >
                論文登録 (Upload)
              </button>
              <button
                onClick={() => setCurrentView('search')}
                className="px-6 py-2 bg-gray-200 text-gray-700 rounded-lg font-semibold hover:bg-gray-300"
              >
                検索 (Search)
              </button>
            </div>

            <div className="border-4 border-dashed border-blue-300 rounded-xl p-12 text-center bg-blue-50">
              <Upload className="w-16 h-16 mx-auto mb-4 text-blue-600" />
              <h2 className="text-2xl font-bold text-gray-800 mb-2">
                研究論文をアップロード (Upload Research Paper)
              </h2>
              <p className="text-gray-600 mb-6">
                PDFファイルをアップロードすると、AIが自動的にMDRCG情報を生成します<br/>
                (AI will automatically generate MDRCG information from your PDF)
              </p>

              <label className="inline-block">
                <input
                  type="file"
                  accept=".pdf"
                  onChange={handleFileUpload}
                  className="hidden"
                />
                <span className="px-8 py-3 bg-blue-600 text-white rounded-lg font-semibold cursor-pointer hover:bg-blue-700 inline-block">
                  PDFを選択 (Select PDF)
                </span>
              </label>

              {uploadedFile && (
                <div className="mt-6 text-left bg-white p-4 rounded-lg">
                  <FileText className="inline-block w-5 h-5 mr-2 text-blue-600" />
                  <span className="font-semibold">{uploadedFile.name}</span>
                </div>
              )}
            </div>

            {isProcessing && (
              <div className="mt-8 text-center">
                <Loader className="w-12 h-12 mx-auto animate-spin text-blue-600 mb-4" />
                <p className="text-lg font-semibold text-gray-700">
                  AIが論文を解析中... (AI is analyzing your paper...)
                </p>
                <p className="text-sm text-gray-600 mt-2">
                  MDRCG情報の自動ドラフトを生成しています
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }

  // フォームビュー (Form View)
  if (currentView === 'form') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-8">
        <div className="max-w-5xl mx-auto">
          <div className="bg-white rounded-2xl shadow-2xl p-8">
            <div className="mb-6">
              <h1 className="text-3xl font-bold text-gray-800 mb-2">
                MDRCG 情報入力フォーム
              </h1>
              <p className="text-gray-600">Research Context Information Form</p>
              <div className="mt-4 p-4 bg-green-50 border-l-4 border-green-500 rounded">
                <div className="flex items-center">
                  <Brain className="w-5 h-5 text-green-600 mr-2" />
                  <p className="text-sm text-green-800">
                    <strong>AIドラフト生成完了！</strong> 以下の内容を確認・修正してください
                    <br/>(AI draft generated! Please review and edit as needed)
                  </p>
                </div>
              </div>
            </div>

            <div className="space-y-6 max-h-[600px] overflow-y-auto pr-4">
              {mdrcgQuestions.map((question) => (
                <div key={question.id} className="border-b pb-6">
                  <label className="block mb-2">
                    <span className="text-lg font-semibold text-gray-800">
                      {question.label}
                      {question.required && <span className="text-red-500 ml-1">*</span>}
                    </span>
                  </label>
                  <textarea
                    value={formData[question.id] || ''}
                    onChange={(e) => handleInputChange(question.id, e.target.value)}
                    placeholder={question.placeholder}
                    className="w-full p-4 border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:outline-none min-h-[120px] bg-yellow-50"
                    required={question.required}
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    💡 AIが生成した内容です。必要に応じて修正してください
                  </p>
                </div>
              ))}
            </div>

            <div className="mt-8 flex gap-4">
              <button
                onClick={() => setCurrentView('upload')}
                className="px-6 py-3 bg-gray-300 text-gray-700 rounded-lg font-semibold hover:bg-gray-400"
              >
                ← 戻る (Back)
              </button>
              <button
                onClick={handleSubmit}
                className="flex-1 px-6 py-3 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700"
              >
                登録する (Submit)
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // 検索ビュー (Search View)
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="bg-white rounded-2xl shadow-2xl p-8">
          <div className="mb-6">
            <h1 className="text-3xl font-bold text-gray-800 mb-2">
              研究データ検索 (Research Data Search)
            </h1>
            <p className="text-gray-600">AI-Powered Smart Search with MDRCG Context</p>
          </div>

          <div className="flex justify-center gap-4 mb-8">
            <button
              onClick={() => setCurrentView('upload')}
              className="px-6 py-2 bg-gray-200 text-gray-700 rounded-lg font-semibold hover:bg-gray-300"
            >
              論文登録 (Upload)
            </button>
            <button
              onClick={() => setCurrentView('search')}
              className="px-6 py-2 bg-blue-600 text-white rounded-lg font-semibold"
            >
              検索 (Search)
            </button>
          </div>

          <div className="mb-6">
            <div className="relative">
              <Search className="absolute left-4 top-4 text-gray-400 w-5 h-5" />
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="キーワード、産業応用、異分野応用などで検索... (Search by keywords, industrial applications, cross-domain uses...)"
                className="w-full pl-12 pr-4 py-4 border-2 border-gray-300 rounded-lg focus:border-blue-500 focus:outline-none text-lg"
              />
            </div>
          </div>

          <button
            onClick={() => setShowFilters(!showFilters)}
            className="flex items-center gap-2 px-4 py-2 bg-gray-200 rounded-lg hover:bg-gray-300 mb-4"
          >
            <Filter className="w-4 h-4" />
            フィルター (Filters)
            {showFilters ? <ChevronUp className="w-4 h-4" /> : <ChevronDown className="w-4 h-4" />}
          </button>

          {showFilters && (
            <div className="mb-6 p-4 bg-gray-50 rounded-lg">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div>
                  <h3 className="font-semibold mb-2">研究分野 (Research Field)</h3>
                  <div className="space-y-2">
                    {['材料科学 (Materials Science)', '生命科学 (Life Sciences)', 'ナノサイエンス (Nanoscience)', '高分子科学 (Polymer Science)', '物性物理学 (Condensed Matter Physics)', '半導体工学 (Semiconductor Engineering)', '文化財科学 (Cultural Heritage Science)', '地球科学 (Earth Science)', '食品科学 (Food Science)'].map(field => (
                      <label key={field} className="flex items-center gap-2">
                        <input
                          type="checkbox"
                          checked={selectedFilters.field.includes(field)}
                          onChange={() => toggleFilter('field', field)}
                          className="w-4 h-4"
                        />
                        <span className="text-sm">{field}</span>
                      </label>
                    ))}
                  </div>
                </div>
                <div>
                  <h3 className="font-semibold mb-2">測定手法 (Measurement Method)</h3>
                  <div className="space-y-2">
                    {['X線回折 (X-ray Diffraction)', 'X線結晶構造解析 (X-ray Crystallography)', '時間分解分光 (Time-Resolved Spectroscopy)', '小角X線散乱 (SAXS)', 'X線磁気円二色性 (XMCD)', '高速X線イメージング (High-Speed X-ray Imaging)', 'X線蛍光分析 (XRF)', '高圧X線回折 (High-Pressure XRD)', '赤外分光 (Infrared Spectroscopy)'].map(method => (
                      <label key={method} className="flex items-center gap-2">
                        <input
                          type="checkbox"
                          checked={selectedFilters.method.includes(method)}
                          onChange={() => toggleFilter('method', method)}
                          className="w-4 h-4"
                        />
                        <span className="text-sm">{method}</span>
                      </label>
                    ))}
                  </div>
                </div>
                <div>
                  <h3 className="font-semibold mb-2">産業応用 (Industrial Application)</h3>
                  <div className="space-y-2">
                    {['自動車産業 (Automotive)', '製薬・医療 (Pharmaceutical)', 'エネルギー産業 (Energy)', '化学産業 (Chemical)', 'エレクトロニクス (Electronics)', '文化・芸術 (Culture & Arts)', '資源探査 (Resource Exploration)', '食品産業 (Food Industry)'].map(app => (
                      <label key={app} className="flex items-center gap-2">
                        <input
                          type="checkbox"
                          checked={selectedFilters.application.includes(app)}
                          onChange={() => toggleFilter('application', app)}
                          className="w-4 h-4"
                        />
                        <span className="text-sm">{app}</span>
                      </label>
                    ))}
                  </div>
                </div>
                <div>
                  <h3 className="font-semibold mb-2">ビームライン (Beamline)</h3>
                  <div className="space-y-2">
                    {['BL19B2', 'BL41XU', 'BL43IR', 'BL40B2', 'BL25SU', 'BL20XU', 'BL08W', 'BL10XU'].map(bl => (
                      <label key={bl} className="flex items-center gap-2">
                        <input
                          type="checkbox"
                          checked={selectedFilters.beamline.includes(bl)}
                          onChange={() => toggleFilter('beamline', bl)}
                          className="w-4 h-4"
                        />
                        <span className="text-sm">{bl}</span>
                      </label>
                    ))}
                  </div>
                </div>
                <div>
                  <h3 className="font-semibold mb-2">年度 (Year)</h3>
                  <div className="space-y-2">
                    {[2024, 2023, 2022].map(year => (
                      <label key={year} className="flex items-center gap-2">
                        <input
                          type="checkbox"
                          checked={selectedFilters.year.includes(year)}
                          onChange={() => toggleFilter('year', year)}
                          className="w-4 h-4"
                        />
                        <span className="text-sm">{year}</span>
                      </label>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          <div className="mb-4">
            <p className="text-gray-600">
              検索結果: <strong>{filteredPapers.length}</strong> 件
            </p>
          </div>

          <div className="space-y-4 max-h-[600px] overflow-y-auto">
            {filteredPapers.map(paper => (
              <div key={paper.id} className="border-2 border-gray-200 rounded-lg p-6 hover:border-blue-500 hover:shadow-lg transition-all">
                <div className="flex justify-between items-start mb-3">
                  <h3 className="text-xl font-bold text-gray-800 flex-1">
                    {paper.title}
                  </h3>
                  <span className="text-sm text-gray-500 ml-4">{paper.year}</span>
                </div>
                <p className="text-sm text-gray-600 mb-3 italic">{paper.titleEn}</p>
                <p className="text-sm text-gray-700 mb-2">
                  <strong>著者 (Authors):</strong> {paper.authors}
                </p>
                <div className="flex flex-wrap gap-2 mb-3">
                  <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-xs font-semibold">
                    {paper.field}
                  </span>
                  <span className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-xs font-semibold">
                    {paper.method}
                  </span>
                  <span className="px-3 py-1 bg-purple-100 text-purple-800 rounded-full text-xs font-semibold">
                    {paper.beamline}
                  </span>
                  <span className="px-3 py-1 bg-orange-100 text-orange-800 rounded-full text-xs font-semibold">
                    {paper.application}
                  </span>
                </div>

                <div className="space-y-2 text-sm">
                  <div>
                    <strong className="text-gray-800">主な結論 (Main Conclusion):</strong>
                    <p className="text-gray-700 mt-1">{paper.mainConclusion}</p>
                  </div>
                  <div>
                    <strong className="text-gray-800">産業応用 (Industrial Application):</strong>
                    <p className="text-gray-700 mt-1">{paper.industrialApplication}</p>
                  </div>
                  <div>
                    <strong className="text-gray-800">異分野展開 (Cross-Domain Potential):</strong>
                    <p className="text-gray-700 mt-1">{paper.crossDomain}</p>
                  </div>
                  <div>
                    <strong className="text-gray-800">失敗事例 (Failed Approach):</strong>
                    <p className="text-gray-700 mt-1">{paper.failedApproach}</p>
                  </div>
                </div>

                <button className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-sm">
                  詳細を見る (View Details)
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
