      ]
    }
  };

  // 高度検索ナレッジツリー (Advanced Search Knowledge Tree)
  const searchKnowledgeTree = {
    'x線': {
      type: 'category',
      label: 'X線技術 (X-ray Technology)',
      branches: {
        'x線回折': {
          type: 'category',
          label: 'X線回折 (X-ray Diffraction)',
          branches: {
            '高エネルギー': {
